import "./App.css";
import React, { useState } from "react";
import Web3 from "web3";
import { Alchemy, Network } from "alchemy-sdk";
import Gallery from "./components/Gallery.jsx";

const config = {
    apiKey: "oj3HEIzyiXxRP3VxIkyw6ytYYwg5pwxA",
    network: Network.ETH_MAINNET,
};
const alchemy = new Alchemy(config);

function App() {
    let web3;
    const [imgURL, setImgURL] = useState("");
    const [name, setName] = useState("");
    const [nftList, setNFTList] = useState([]);
    const [gallery, setGallery] = useState();
    const connectWalletHandler = async () => {
        if (
            typeof window !== "undefined" &&
            typeof window.ethereum !== "undefined"
        ) {
            try {
                await window.ethereum.request({
                    method: "eth_requestAccounts",
                });
                web3 = new Web3(window.ethereum);
            } catch (e) {
                console.log(e.message);
            }
        } else {
            alert("MetaMask not installed");
        }
    };
    const loadNFTHandler = async () => {
        const nfts = await alchemy.nft.getNftsForOwner(
            "0x5e765C6A318502FF2F6eF0D951e84F8dAE7FA3c9"
        );
        setImgURL(nfts.ownedNfts[0].media[0].gateway);
        setName(nfts.ownedNfts[0].title);
        // console.log(nfts.ownedNfts[0]);
        let list = [nfts.ownedNfts[0], nfts.ownedNfts[1], nfts.ownedNfts[2]];
        setNFTList(list);
        setGallery(<Gallery nftList={nftList}></Gallery>);
    };
    return (
        <div className="App">
            <h1>Welcome</h1>
            <button onClick={connectWalletHandler}>Connect Wallet</button>
            <button onClick={loadNFTHandler}>Load NFTs</button>
            {gallery}
        </div>
    );
}

export default App;
